          </Grid>
        </Grid>
        <Grid item md="12" xs="12" style={{
      marginTop: "3%", marginLeft: 0, marginRight:0
    }} ref={ContactRef}>
          <Contact />
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
