import React, { useState, useEffect } from "react";
import { Grid, Typography, CardMedia } from "@material-ui/core";
import '@fontsource/roboto';
import ProfilePic from "../krystal.png";

const About = () => {

    //Nav Rendering for Smartphone vs Laptop
    const [isDesktop, setDesktop] = useState(window.innerWidth > 1000);
    const updateMedia = () => {
        setDesktop(window.innerWidth > 1000);
    };
    useEffect(() => {
        window.addEventListener("resize", updateMedia);
        return () => window.removeEventListener("resize", updateMedia);
    });

    //Style
    const style = {
        font2: {
            color: "#730099",
            margin: 20,
            fontSize: 18
        },
        font3: {
            color: "white",
            fontSize: 20,
            borderBottom: "3px solid #730099",
            borderRight: "3px solid #730099",
            backgroundColor: "#FDB7C2",
            margin: "auto",
            borderRadius: 5,
            width: "60%"
        },
        picture: {
            width: (!isDesktop) ? "80%" : "65%",
            height: "auto",
            marginTop: (!isDesktop) ? 0 : 10,
            marginBottom: (!isDesktop) ? 0 : 0,
            borderRadius: 60,
            border: "2px solid #730099"
        },
        picture2: {
            width: (!isDesktop) ? "100%" : "30%",
            height: "auto"
        },
        title: {
            fontSize: (!isDesktop) ? 15 : 25,
            color: "#730099"
        },
        section: {
            marginTop: 0,
            height: (!isDesktop) ? "auto" : 700
        },
        card: {
            margin: 20,
            width: "auto%",
            height: "auto",
            marginBottom: 20,
            borderRadius: 20
        },
        card2: {
            marginBottom: 50
        },
        cardMessage: {
            display: 'fixed',  
            justifyContent:'center', 
            alignItems:'center',
            height: (!isDesktop) ? "60%" : "80%",
            width: (!isDesktop) ? "97%" : "auto",
            margin: (!isDesktop) ? 0 : 100,
            marginTop: (!isDesktop) ? 0 : 100,
            textAlign: "center",
            border: "5px solid pink",
            backgroundColor: "white"
        }
    }
    return (
        <Grid container direction="row" alignItems="center">
            <Grid item md="6" xs="12" style={style.section}>

                <div style={style.card}>
                    <CardMedia>
                        <img src={ProfilePic} style={style.picture}/>
                    </CardMedia> 
                </div>{(!isDesktop)
                        ?
                        <>
                            <Typography variant="h4" style={style.title}>Krystal Smith, MSN, APRN, CNM</Typography>
                        </>
                        : <Typography variant="h4" style={style.title}>Krystal Smith, MSN, APRN, CNM</Typography>
                    }
            </Grid>
            <Grid item md="6" xs="12" style={style.section}>

                <div style={style.card2}>
                    <Typography style={style.font2}>I have 2 children and an amazing dog named Charlie. Gosh it feels like it was just yesterday we got him and since then he has been such a blessing in our lives. I think what he has taught me is that Birth and Motherhood are my hobbies, being a Fur Mommy is my passion. It's what makes me come home everyday. I love you Charlie!</Typography>
                </div>
                <div style={style.card2}>
                    <Typography variant="h6" style={style.font3}>Experience</Typography>
                    <Typography style={style.font2}>Masterâ€™s degree in the Science of Nurse-Midwifery (2021)</Typography>
                    <Typography style={style.font2}>Registered Nurse in Labor &amp; Delivery and Postpartum for 5.5 years (2016)</Typography>
                    <Typography style={style.font2}>Certified Nurse Midwife through AMCB (2021)</Typography>
                    <Typography style={style.font2}>APRN Kentucky License (2021)</Typography>
                    <Typography style={style.font2}>Midwifery Clinical Experience in the Home Setting (2020)</Typography>
                    <Typography style={style.font2}>Neonatal resuscitation certified</Typography>
                    <Typography style={style.font2}>BLS CPR certified</Typography>
                </div>
            </Grid>
        </Grid>
    )
}

export default About;
