import React, { useState, useEffect } from "react";
import "../App.css"
import { Grid, Typography, AppBar, Toolbar, IconButton, Button, Modal, Card } from "@material-ui/core";
import ContactForm from "./ContactForm";
import '@fontsource/roboto';
import ProfilePic from "../Kaily1.png";
import MenuIcon from "@material-ui/icons/Menu";
import FacebookIcon from '@material-ui/icons/Facebook';
import EmailIcon from '@material-ui/icons/Email';
import HomeIcon from '@material-ui/icons/Home';

const MessageModal = ({ name, isConsult, openForm, handleOpen, handleClose, consultSnack }) => {

    //Nav Rendering for Smartphone vs Laptop
    const [isDesktop, setDesktop] = useState(window.innerWidth > 1000);
    const updateMedia = () => {
        setDesktop(window.innerWidth > 1000);
    };
    useEffect(() => {
        window.addEventListener("resize", updateMedia);
        return () => window.removeEventListener("resize", updateMedia);
    });

    //Style
    const style = {
        aboutFont: {
            color: "#730099",
            padding: (!isDesktop) ? "10px 10px 20px 10px" : "10px 40px 20px 40px",
            fontSize: 18,
            marginTop: 10
        },
        experienceFont: {
            color: "#730099",
            padding: (!isDesktop) ? "10px 10px 20px 10px" : "10px 0px 20px 0px",
            fontSize: 18
        },
        subTitle: {
            color: "white",
            fontSize: 20,
            borderBottom: "3px solid #730099",
            borderRight: "3px solid #730099",
            backgroundColor: "#FDB7C2",
            margin: "auto",
            marginBottom: 5,
            marginTop: 10,
            borderRadius: 5,
            width: 300
        },
        picture: {
            width: (!isDesktop) ? 200 : 250,
            height: "auto",
            marginTop: 30,
            marginBottom: (!isDesktop) ? 0 : 0,
            borderRadius: 40,
            border: "2px solid #730099"
        },
        title: {
            fontSize: (!isDesktop) ? 20 : 20,
            color: "#730099",
            margin: "10px 0px 10px 0px",
            fontWeight: "bold"
        },
        section: {
            padding: "20px 0px 50px 0px",
            height: "auto"
        },
        newPatient: {
            backgroundColor: "#d9b3ff",
            color: "white",
            width: (!isDesktop) ? 120 : "30%",
            height: (!isDesktop) ? 35 : 50,
            fontSize: (!isDesktop) ? 8 : 14,
            fontWeight: "bold",
            marginLeft: (!isDesktop) ? 10 : "40%",
            borderRadius: 200,
            border: "3px outset #E1F0FA"
        },
            contactCard: {
            display: 'fixed',  
            justifyContent:'center', 
            alignItems:'center',
            height: (!isDesktop) ? "80%" : "80%",
            width: (!isDesktop) ? "96%" : "auto",
            margin: (!isDesktop) ? "0.5%" : 100,
            marginTop: (!isDesktop) ? 0 : 100,
            textAlign: "center",
            border: "5px solid purple",
            overflowY: "auto"
        }
    }

    return (
        <Grid item md="6" xs="6">
          <Button onClick={handleOpen} style={style.newPatient}>{name}</Button>
          <Modal
              open={openForm}
              onClose={handleClose}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
              >
              <Card style={style.contactCard}>
                <ContactForm closeForm={handleClose} successConsult={consultSnack}/>
              </Card>
            </Modal>
        </Grid>
    //     <Grid item md="6" xs="6">
    //       <Button onClick={messageOpen} style={style.message}>Send Message</Button>
    //       <Modal
    //           open={openMessage}
    //           onClose={messageClose}
    //           aria-labelledby="simple-modal-title"
    //           aria-describedby="simple-modal-description"
    //           >
    //           <Card style={style.messageCard}>
    //             <MessageForm closeMessage={messageClose} successMessage={messageSnack}/>
    //           </Card>
    //       </Modal>
    //    </Grid>
    )
}

export default MessageModal;
